from rest_framework import serializersfrom main.models import *class AboutUsModelSerializer(serializers.ModelSerializer):    class Meta:        model = AboutUsModel        fields = "__all__"class ProductsModelSerializer(serializers.ModelSerializer):    class Meta:        model = ProductsModel        fields = "__all__"class TechnologiesModelSerializer(serializers.ModelSerializer):    class Meta:        model = TechnologiesModel        fields = "__all__"class ServiceItemsModelSerializer(serializers.ModelSerializer):    class Meta:        model = ServiceItemsModel        fields = "__all__"class ServiceModelSerializer(serializers.ModelSerializer):    items = ServiceItemsModelSerializer(many=True)    class Meta:        model = ServiceModel        fields = "__all__"class DocumentsModelSerializer(serializers.ModelSerializer):    class Meta:        model = DocumentsModel        fields = "__all__"class ContactsModelSerializer(serializers.ModelSerializer):    class Meta:        model = ContactsModel        fields = "__all__"class OurPartnersModelSerializer(serializers.ModelSerializer):    class Meta:        model = OurPartnersModel        fields = "__all__"class OurStatisticsModelSerializer(serializers.ModelSerializer):    class Meta:        model = OurStatisticsModel        fields = "__all__"